reporting.reportgraph.GraphQuery:
DECLARE @CodeName AS NVARCHAR(MAX) = N'Facebook.page_posts_impressions_unique', @CodeName2 AS NVARCHAR(MAX) = N'Facebook.page_posts_impressions_paid_unique', @CodeName3 AS NVARCHAR(MAX) = N'Facebook.page_posts_impressions_organic_unique', @CodeName4 AS NVARCHAR(MAX) = N'Facebook.page_posts_impressions_viral_unique';
SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'Week');
IF (@ExternalID IS NOT NULL) AND (@ExternalID != N'') AND EXISTS(SELECT [InsightHitID] FROM [SM_Insight] INNER JOIN [SM_InsightHit_Week] ON [SM_InsightHit_Week].[InsightHitInsightID] = [SM_Insight].[InsightID] WHERE ([InsightCodeName] = @CodeName OR [InsightCodeName] = @CodeName2 OR [InsightCodeName] = @CodeName3 OR [InsightCodeName] = @CodeName4) AND ([InsightPeriodType] = @PeriodType) AND ([InsightExternalID] = @ExternalID) AND ([InsightHitPeriodFrom] BETWEEN @FromDate AND @ToDate) ) BEGIN SELECT CONVERT (NVARCHAR(2),DATEPART(wk,[Date]))+'/'+CONVERT (NVARCHAR(4),DATEPART(YEAR,[Date])) AS [StartTime], [T1].[Values] AS [Unique], [T2].[Values] AS [Paid unique], [T3].[Values] AS [Organic unique], [T4].[Values] AS [Viral unique] FROM {%DatabaseSchema%}.Func_Analytics_EnsureDates (@FromDate,@ToDate,'Week') AS [Dates] LEFT JOIN (SELECT [InsightHitPeriodFrom] AS [StartTime], [InsightHitValue] AS [Values] FROM [SM_Insight] INNER JOIN [SM_InsightHit_Week] ON [SM_InsightHit_Week].[InsightHitInsightID] = [SM_Insight].[InsightID] WHERE ([InsightCodeName] = @CodeName) AND ([InsightPeriodType] = @PeriodType) AND (InsightExternalID = @ExternalID) ) AS [T1] ON [T1].[StartTime] = [Date] LEFT JOIN (SELECT [InsightHitPeriodFrom] AS [StartTime], [InsightHitValue] AS [Values] FROM [SM_Insight] INNER JOIN [SM_InsightHit_Week] ON [SM_InsightHit_Week].[InsightHitInsightID] = [SM_Insight].[InsightID] WHERE ([InsightCodeName] = @CodeName2) AND ([InsightPeriodType] = @PeriodType) AND (InsightExternalID = @ExternalID) ) AS [T2] ON [T2].[StartTime] = [Date] LEFT JOIN (SELECT [InsightHitPeriodFrom] AS [StartTime], [InsightHitValue] AS [Values] FROM [SM_Insight] INNER JOIN [SM_InsightHit_Week] ON [SM_InsightHit_Week].[InsightHitInsightID] = [SM_Insight].[InsightID] WHERE ([InsightCodeName] = @CodeName3) AND ([InsightPeriodType] = @PeriodType) AND (InsightExternalID = @ExternalID) ) AS [T3] ON [T3].[StartTime] = [Date] LEFT JOIN (SELECT [InsightHitPeriodFrom] AS [StartTime], [InsightHitValue] AS [Values] FROM [SM_Insight] INNER JOIN [SM_InsightHit_Week] ON [SM_InsightHit_Week].[InsightHitInsightID] = [SM_Insight].[InsightID] WHERE ([InsightCodeName] = @CodeName4) AND ([InsightPeriodType] = @PeriodType) AND (InsightExternalID = @ExternalID) ) AS [T4] ON [T4].[StartTime] = [Date] END
