reporting.reportgraph.GraphQuery:
EXEC Proc_Analytics_RemoveTempTable CREATE TABLE #AnalyticsTempTable ( StartTime DATETIME, Hits INT, Name NVARCHAR(300) );
SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'week'); INSERT INTO #AnalyticsTempTable (StartTime, Hits, Name)
SELECT [Date] AS StartTime ,T1.Hits AS Hits ,T1.Name AS Name FROM {%DatabaseSchema%}.Func_Analytics_EnsureDates (@FromDate,@ToDate,'Week') AS Dates LEFT JOIN (SELECT HitsStartTime AS Interval, SUM(HitsCount) AS Hits ,ABVariantDisplayName AS Name FROM Analytics_Statistics INNER JOIN Analytics_WeekHits ON StatisticsID = Analytics_WeekHits.HitsStatisticsID INNER JOIN OM_ABVariantData ON ABVariantGUID = CAST(SUBSTRING(StatisticsCode, LEN(StatisticsCode)-35, LEN(StatisticsCode)) AS uniqueidentifier) INNER JOIN OM_ABTest ON ABVariantTestID = ABTestID AND ABTestSiteID = @CMSContextCurrentSiteID WHERE StatisticsSiteID = @CMSContextCurrentSiteID AND StatisticsCode LIKE 'abconversion;%' AND (@TestName IS NULL OR @TestName = '' OR @TestName = SUBSTRING(StatisticsCode, 14, CHARINDEX(';',StatisticsCode,14)-14)) AND (@ConversionName IS NULL OR @ConversionName = '' OR @ConversionName = StatisticsObjectName) GROUP BY HitsStartTime,ABVariantDisplayName ) AS T1 ON Dates.Date=T1.Interval;
EXEC Proc_Analytics_Pivot 'week' EXEC Proc_Analytics_RemoveTempTable
