reporting.reportgraph.GraphQuery:
EXEC Proc_Analytics_RemoveTempTable SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'day');
CREATE TABLE #AnalyticsTempTable ( StartTime DATETIME, Hits DECIMAL(15,2), Name NVARCHAR(300) COLLATE database_default );
-- SELECT INTO statement cannot be used, because it is not supported by Azure SQL INSERT INTO #AnalyticsTempTable SELECT [Date] AS StartTime, T1.Hits, StatisticsObjectName AS Name FROM {%DatabaseSchema%}.Func_Analytics_EnsureDates(@FromDate,@ToDate,'day') AS Dates LEFT JOIN (SELECT HitsStartTime AS StartTime,SUM(HitsCount) AS hits,StatisticsObjectName FROM Analytics_Statistics INNER JOIN Analytics_DayHits ON Analytics_DayHits.HitsStatisticsID = Analytics_Statistics.StatisticsID WHERE (StatisticsSiteID = @CMSContextCurrentSiteID) AND (StatisticsCode = @CodeName) GROUP BY HitsStartTime,StatisticsObjectName) AS T1 ON T1.StartTime = [Date]
EXEC Proc_Analytics_Pivot 'day'
