reporting.reporttable.TableQuery:
/* Variables used to calculate median value. */ DECLARE @BottomHalf float DECLARE @TopHalf float DECLARE @median float
/* Table of sales per customer. */ DECLARE @medianCustomerTable TABLE( CustomerID int, SumOfOrderTotalPrice float)
INSERT INTO @medianCustomerTable( CustomerID, SumOfOrderTotalPrice) SELECT CustomerID AS ID, SUM(OrderGrandTotalInMainCurrency) AS TotalSum FROM COM_Order LEFT JOIN COM_Customer ON OrderCustomerID = CustomerID WHERE OrderSiteID = @CMSContextCurrentSiteID AND (CustomerID IN (SELECT OrderCustomerID FROM COM_Order WHERE OrderSiteID = @CMSContextCurrentSiteID)) AND (ISNULL(OrderIsPaid,0) = 1 OR ISNULL(@OnlyPaid,0) = 0) AND (Orderdate >= @FromDate OR @FromDate IS NULL) AND (OrderDate <= DATEADD(day,1,@ToDate) OR @ToDate IS NULL) GROUP BY CustomerID
SET @BottomHalf = (SELECT MAX(SumOfOrderTotalPrice) FROM (SELECT TOP 50 PERCENT SumOfOrderTotalPrice FROM @medianCustomerTable ORDER BY SumOfOrderTotalPrice) AS BottomHalf) SET @TopHalf = (SELECT MIN(SumOfOrderTotalPrice) FROM (SELECT TOP 50 PERCENT SumOfOrderTotalPrice FROM @medianCustomerTable ORDER BY SumOfOrderTotalPrice DESC) AS TopHalf) SET @median = (@BottomHalf + @TopHalf)/2
SELECT '{$com.report.averagecustomervolumeofsales$}' AS '{$rep.webparrts.parametername$}', CAST(ISNULL(AVG(SumOfOrderTotalPrice),0) AS DECIMAL(38,1)) AS '{$com.report.averagevalue$}', CAST(ISNULL(@median,0) AS DECIMAL(38,1)) AS '{$com.report.medianvalue$}' FROM @medianCustomerTable
