reporting.reporttable.TableQuery:
/* Variables used to calculate median value. */ DECLARE @BottomHalf float DECLARE @TopHalf float DECLARE @median float
/* Table with the sales per order. */ DECLARE @medianPriceTable TABLE( SumOfOrderTotalPrice float )
INSERT INTO @medianPriceTable( SumOfOrderTotalPrice) SELECT ISNULL(ROUND(OrderGrandTotalInMainCurrency,1),0) AS AVGs FROM Com_Order WHERE OrderSiteID = @CMSContextCurrentSiteID AND (Orderdate >= @FromDate OR @FromDate IS NULL) AND (OrderDate <= DATEADD(day,1,@ToDate) OR @ToDate IS NULL) AND (ISNULL(@IsPaid,0) = 0 OR ISNULL(OrderIsPaid, 0) = 1)
SET @BottomHalf = (SELECT MAX(SumOfOrderTotalPrice) FROM (SELECT TOP 50 PERCENT SumOfOrderTotalPrice FROM @medianPriceTable ORDER BY SumOfOrderTotalPrice) AS BottomHalf) SET @TopHalf = (SELECT MIN(SumOfOrderTotalPrice) FROM (SELECT TOP 50 PERCENT SumOfOrderTotalPrice FROM @medianPriceTable ORDER BY SumOfOrderTotalPrice DESC) AS TopHalf) SET @median = (@BottomHalf + @TopHalf)/2
SELECT '{$com.resource.OrderTotalPrice$}' AS '{$rep.webparrts.parametername$}', CAST(ISNULL(AVG(SumOfOrderTotalPrice),0) AS DECIMAL(38,1)) AS 'Average value', CAST(ISNULL(@median,0) AS DECIMAL(38,1)) AS 'Median value' FROM @medianPriceTable
