reporting.reporttable.TableQuery:
DECLARE @PageViews TABLE ( PagePath NVARCHAR(500), Pageviews INT, Percents DECIMAL(10,2) )
DECLARE @Sum DECIMAL;
SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'day'); SET @ToDate ={%DatabaseSchema%}.Func_Analytics_EndDateTrim(@ToDate,'day');
SELECT @Sum = SUM(HitsCount) FROM Analytics_Statistics INNER JOIN Analytics_DayHits ON Analytics_DayHits.HitsStatisticsID = Analytics_Statistics.StatisticsID WHERE (StatisticsSiteID = @CMSContextCurrentSiteID) AND (StatisticsCode=@CodeName) AND (HitsStartTime >= @FromDate) AND (HitsEndTime <= @ToDate)
INSERT INTO @PageViews (PagePath, PageViews, Percents) SELECT TOP 100 StatsWithPath.NodeAliasPath, SUM(StatsWithPath.HitsCount) AS PageViews, (SUM(StatsWithPath.HitsCount)/@Sum)*100 AS Percents FROM ( SELECT StatisticsObjectName AS NodeAliasPath, StatisticsObjectID AS ObjectID, HitsCount FROM Analytics_Statistics INNER JOIN Analytics_DayHits ON Analytics_DayHits.HitsStatisticsID = Analytics_Statistics.StatisticsID WHERE (StatisticsSiteID = @CMSContextCurrentSiteID) AND (HitsStartTime >= @FromDate) AND (HitsEndTime <= @ToDate) AND (StatisticsCode = @CodeName) ) AS StatsWithPath GROUP BY StatsWithPath.NodeAliasPath, StatsWithPath.ObjectID ORDER BY PageViews DESC
SELECT PagePath AS '{$reports_exitpage_Day.path_header$}', PageViews AS '{$reports_exitpage_Day.hits_header$}', CAST (Percents AS NVARCHAR(10))+'%' AS '{$reports_exitpage_Day.hits_percent_header$}' FROM @PageViews ORDER BY PageViews DESC
