reporting.reporttable.TableQuery:
SET @ToDate ={%DatabaseSchema%}.Func_Analytics_EndDateTrim(@ToDate,'day'); SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'day'); DECLARE @myselection TABLE ( NodeAliasPath varchar(450), StatisticsObjectCulture varchar(400), Count float );
DECLARE @countedAll TABLE ( Counted float );
INSERT INTO @myselection SELECT TOP 100 StatisticsObjectName AS '{$reports_pageviews_day.path_header$}', StatisticsObjectCulture AS '{$general.culture$}', SUM(HitsCount) AS '{$reports_pageviews_day.hits_header$}' FROM Analytics_Statistics INNER JOIN Analytics_DayHits ON Analytics_DayHits.HitsStatisticsID = Analytics_Statistics.StatisticsID WHERE (StatisticsSiteID = @CMSContextCurrentSiteID) AND (StatisticsCode=@CodeName) AND (HitsStartTime >= @FromDate) AND (HitsEndTime <= @ToDate) GROUP BY StatisticsObjectCulture, StatisticsObjectName ORDER BY SUM(HitsCount) DESC
INSERT INTO @countedAll SELECT SUM(HitsCount) AS '{$reports_pageviews_day.hits_header$}' FROM Analytics_Statistics INNER JOIN Analytics_DayHits ON Analytics_DayHits.HitsStatisticsID = Analytics_Statistics.StatisticsID WHERE (StatisticsSiteID = @CMSContextCurrentSiteID) AND (StatisticsCode=@CodeName) AND (HitsStartTime >= @FromDate) AND (HitsEndTime <= @ToDate)
SELECT NodeAliasPath AS '{$reports_exitpage_day.path_header$}', StatisticsObjectCulture AS '{$general.culture$}', Count AS '{$reports_exitpage_day.hits_header$}', CAST (CAST((100*Count)/(SELECT Counted FROM @countedAll) as decimal(10,2)) AS NVARCHAR(10))+'%' as '{$reports_exitpage_day.hits_percent_header$}' FROM @myselection
