reporting.reporttable.TableQuery:
DECLARE @cols AS NVARCHAR(MAX), @sql AS NVARCHAR(MAX), @CodeName AS NVARCHAR(MAX) = 'Facebook.page_fans_city';
SET @FromDate = {%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'day');
IF (@ExternalID IS NOT NULL) AND (@ExternalID != N'') BEGIN SET @cols = STUFF( (SELECT N',' + QUOTENAME([col]) AS [text()] FROM (SELECT DISTINCT [InsightValueName] AS [col] FROM [SM_Insight] INNER JOIN [SM_InsightHit_Day] ON [InsightID] = [InsightHitInsightID] WHERE [InsightExternalID] = @ExternalID AND [InsightPeriodType] = @PeriodType AND [InsightCodeName] = @CodeName AND [InsightHitPeriodFrom] BETWEEN @FromDate AND @ToDate) AS [T1] ORDER BY [col] FOR XML PATH('')), 1, 1, N'');
SET @sql = N'SELECT * FROM ( SELECT CONVERT(NVARCHAR, [Date], 111) AS ''{$general.date$}'', [InsightValueName], [InsightHitValue] FROM [SM_Insight] INNER JOIN [SM_InsightHit_Day] ON [InsightID] = [InsightHitInsightID] AND [InsightCodeName] = @CodeName AND [InsightPeriodType] = @PeriodType AND InsightExternalID = @ExternalID INNER JOIN {%DatabaseSchema%}.Func_Analytics_EnsureDates (@FromDate,@ToDate,''day'') ON [Date] = [InsightHitPeriodFrom] ) AS [T2] PIVOT ( SUM(InsightHitValue) FOR [InsightValueName] IN(' + @cols + N') ) AS [T3] ;';
EXEC sp_executesql @sql, N'@FromDate DATE, @ToDate DATE, @CodeName AS NVARCHAR(MAX), @ExternalID AS NVARCHAR(MAX), @PeriodType AS NVARCHAR(20)', @FromDate, @ToDate, @CodeName, @ExternalID, @PeriodType;
END ELSE BEGIN SELECT 0 AS [StartTime] WHERE 0 = 1 END
