reporting.reporttable.TableQuery:
DECLARE @Sum DECIMAL;
SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'day'); SET @ToDate ={%DatabaseSchema%}.Func_Analytics_EndDateTrim(@ToDate,'day');
SELECT @Sum = SUM(HitsCount) FROM Analytics_Statistics, Analytics_DayHits WHERE (StatisticsSiteID = @CMSContextCurrentSiteID) AND (StatisticsCode=@CodeName) AND (StatisticsID = HitsStatisticsID) AND (HitsStartTime >= @FromDate) AND (HitsEndTime <= @ToDate)
SELECT TOP 100 StatisticsObjectName AS '{$reports_referrals_day.name_header$}', SUM(HitsCount) AS '{$reports_referrals_day.hits_header$}', CAST (CAST ((SUM(HitsCount) / @Sum) *100 AS DECIMAL(10,2)) AS NVARCHAR(8)) +'%' AS '{$reports_referrals_day.percent_header$}' FROM Analytics_Statistics, Analytics_DayHits WHERE (StatisticsSiteID = @CMSContextCurrentSiteID) AND (StatisticsCode=@CodeName) AND (StatisticsID = HitsStatisticsID) AND (HitsStartTime >= @FromDate) AND (HitsEndTime <= @ToDate) GROUP BY StatisticsObjectName ORDER BY SUM(HitsCount) DESC
