reporting.reporttable.TableQuery:
SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'week'); SET @ToDate ={%DatabaseSchema%}.Func_Analytics_EndDateTrim(@ToDate,'week'); DECLARE @myselection TABLE ( NodeAliasPath varchar(400), StatisticsObjectCulture varchar(400), Count float );
DECLARE @countedAll TABLE ( Counted float );
INSERT INTO @myselection SELECT TOP 100 StatisticsObjectName, StatisticsObjectCulture, SUM(HitsCount) AS Count FROM Analytics_Statistics INNER JOIN Analytics_WeekHits ON Analytics_WeekHits.HitsStatisticsID = Analytics_Statistics.StatisticsID WHERE (StatisticsSiteID = @CMSContextCurrentSiteID) AND (StatisticsCode=@CodeName) AND (HitsStartTime >= @FromDate) AND (HitsEndTime <= @ToDate) GROUP BY StatisticsObjectCulture, StatisticsObjectName ORDER BY SUM(HitsCount) DESC
INSERT INTO @countedAll SELECT SUM(HitsCount) AS Counted FROM Analytics_Statistics INNER JOIN Analytics_WeekHits ON Analytics_WeekHits.HitsStatisticsID = Analytics_Statistics.StatisticsID WHERE (StatisticsSiteID = @CMSContextCurrentSiteID) AND (StatisticsCode=@CodeName) AND (HitsStartTime >= @FromDate) AND (HitsEndTime <= @ToDate)
SELECT NodeAliasPath AS '{$reports_landingpage_Week.path_header$}', StatisticsObjectCulture AS '{$general.culture$}', Count AS '{$reports_landingpage_Week.hits_header$}', CAST (CAST((100*Count)/(SELECT Counted FROM @countedAll) as decimal(10,2)) AS NVARCHAR(10))+'%' AS '{$reports_landingpage_Week.hits_percent_header$}' FROM @myselection
