reporting.reporttable.TableQuery:
SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'day'); SET @ToDate ={%DatabaseSchema%}.Func_Analytics_EndDateTrim(@ToDate,'day'); DECLARE @myselection TABLE ( NodeAliasPath varchar(400), ObjectID INT, Culture varchar(400), Count float, Average int );
DECLARE @countedAll TABLE ( Counted float );
INSERT INTO @myselection (NodeAliasPath,ObjectID,Culture,Count) SELECT TOP 100 StatisticsObjectName, StatisticsObjectID, StatisticsObjectCulture, SUM(HitsCount) AS Count FROM Analytics_Statistics INNER JOIN Analytics_DayHits ON Analytics_DayHits.HitsStatisticsID = Analytics_Statistics.StatisticsID WHERE (StatisticsSiteID = @CMSContextCurrentSiteID) AND (StatisticsCode=@CodeName) AND (HitsStartTime >= @FromDate) AND (HitsEndTime <= @ToDate) GROUP BY StatisticsObjectCulture, StatisticsObjectName, StatisticsObjectID ORDER BY SUM(HitsCount) DESC
INSERT INTO @countedAll SELECT SUM(HitsCount) AS Counted FROM Analytics_Statistics INNER JOIN Analytics_DayHits ON Analytics_DayHits.HitsStatisticsID = Analytics_Statistics.StatisticsID WHERE (StatisticsSiteID = @CMSContextCurrentSiteID) AND (StatisticsCode=@CodeName) AND (HitsStartTime >= @FromDate) AND (HitsEndTime <= @ToDate)
UPDATE @myselection SET Average = (SELECT SUM (HitsValue) / SUM (HitsCount) FROM Analytics_HourHits JOIN Analytics_Statistics ON HitsStatisticsID = StatisticsID WHERE HitsStartTime >= @FromDate AND HitsEndTime <= @ToDate AND NodeAliasPath = StatisticsObjectName AND StatisticsCode ='avgtimeonpage' AND StatisticsSiteID = @CMSContextCurrentSiteID AND culture = Analytics_Statistics.StatisticsObjectCulture )
SELECT NodeAliasPath AS '{$reports_pageviews_Day.path_header$}', culture AS '{$general.culture$}', Count AS '{$reports_pageviews_Day.hits_header$}', CAST (CAST((100*Count)/(SELECT Counted FROM @countedAll) as decimal(10,2))AS NVARCHAR(10))+'%' AS '{$reports_pageviews_Day.hits_percent_header$}', ISNULL(CONVERT(varchar, DATEADD(s, average, 0), 108),'00:00:00') AS '{$reports_pageviews.average$}' FROM @myselection
