reporting.reporttable.TableQuery:
DECLARE @PaveViews TABLE ( NodeAliasPath NVARCHAR(450), Pageviews INT, Percents DECIMAL(10,2) )
DECLARE @Sum DECIMAL;
SET @FromDate ={%DatabaseSchema%}.Func_Analytics_DateTrim(@FromDate,'day'); SET @ToDate ={%DatabaseSchema%}.Func_Analytics_EndDateTrim(@ToDate,'day');
SELECT @Sum = SUM(HitsCount) FROM Analytics_Statistics INNER JOIN Analytics_DayHits ON Analytics_DayHits.HitsStatisticsID = Analytics_Statistics.StatisticsID LEFT JOIN View_CMS_Tree_Joined ON View_CMS_Tree_Joined.NodeID = Analytics_Statistics.StatisticsObjectID AND DocumentCulture = StatisticsObjectCulture WHERE (StatisticsSiteID = @CMSContextCurrentSiteID) AND (StatisticsCode=@CodeName) AND (HitsStartTime >= @FromDate) AND (HitsEndTime <= @ToDate)
INSERT INTO @PaveViews (NodeAliasPath,pageViews,percents) SELECT TOP 10 CASE WHEN NodeAliasPath LIKE '' OR NodeAliasPath IS NULL THEN StatisticsObjectName ELSE NodeAliasPath END , SUM(HitsCount), (SUM(HitsCount)/@Sum)*100 FROM Analytics_Statistics INNER JOIN Analytics_DayHits ON Analytics_DayHits.HitsStatisticsID = Analytics_Statistics.StatisticsID LEFT JOIN View_CMS_Tree_Joined ON Analytics_Statistics.StatisticsObjectID = View_CMS_Tree_Joined.NodeID AND DocumentCulture = StatisticsObjectCulture WHERE (StatisticsSiteID = @CMSContextCurrentSiteID) AND (HitsStartTime >= @FromDate) AND (HitsEndTime <= @ToDate) AND (StatisticsCode=@CodeName) GROUP BY NodeAliasPath, StatisticsObjectName ORDER BY SUM(HitsCount) DESC
SELECT NodeAliasPath AS '{$reports_landingpage_Day.path_header$}',pageviews AS '{$reports_landingpage_Day.hits_header$}', CAST (Percents AS NVARCHAR(10))+'%' AS '{$reports_landingpage_Day.hits_percent_header$}' FROM @PaveViews ORDER BY PageViews DESC
